export function Logo({ variant = 'default', width = 30, ...others }) {
  const theme = useMantineTheme();

  return (
    <svg
      {...others}
      width={width}
      height={width * 0.8}
      viewBox='0 0 30 24'
      fill='none'
      xmlns='http://www.w3.org/2000/svg'
    >
      <path
        d='M15.0111 0H0V4.12033V11.9453C0 11.9453 0 11.9453 0 12.1714C0 18.0857 4.99139 24 15.0185 24C25.0381 24 30 18.1222 30 12.1714C30 6.2206 25.1415 0 15.0111 0ZM19.5742 13.4257C16.5026 13.433 13.4457 13.01 10.4849 12.1641L10.7728 10.4649C13.6082 11.2671 16.5321 11.6901 19.4634 11.7265L19.5742 13.4257Z'
        fill={variant === 'default' ? theme.colors[theme.primaryColor] : '#FFF'}
      />
    </svg>
  );
}

